import React, { Component } from 'react';
import './Calculator.css';

const dados = {
    valorAInput: "",
    valorBInput: "",
    resultado: ""
};

//Pure Component or Class Component
class NewCalculator extends Component {

  render() {

    return(
        <div>
            <div class="fundo">
                <h1>Calculator</h1>
                <div class="grupo"> 
                <div id="DivA">
                    Valor 1:
                    <br/>
                    <input type="number" valorA={dados.valorAInput} onChange={this.handleValorAFieldChange} />
                </div>
                <div id="DivB">
                    Valor 2:
                    <br/>
                    <input type="number" valorB={dados.valorBInput} onChange={this.handleValorBFieldChange} />
                </div>
                </div>

                <br/><br/><br/><br/>

                <button class="button" value="+" onClick={this.handleButtonClick}>+</button>
                <button class="button" value="-" onClick={this.handleButtonClick}>-</button>
                <button class="button" value="*" onClick={this.handleButtonClick}>*</button>
                <button class="button" value="/" onClick={this.handleButtonClick}>/</button>
                <button class="buttonRed" value="C" onClick={this.handleButtonClick}>C</button>

                <br/><br/>

                <div id="Result">
                    Resultado: {dados.resultado}
                </div>
                <br/>
            </div>
        </div>
    );
  }

  handleButtonClick = (arg) => {
    const operation = arg.target.value;

    switch(operation){
        case "+":
            this.setState({
            resultado: parseFloat(this.props.valorA) + parseFloat(this.props.valorB)
            })
            break
        case "-":
            this.setState({
            resultado: parseFloat(this.props.valorA) - parseFloat(this.props.valorB)
            })
            break
        case "*":
            this.setState({
            resultado: parseFloat(this.props.valorA) * parseFloat(this.props.valorB)
            })
            break
        case "/":
            this.setState({
            resultado: parseFloat(this.props.valorA) / parseFloat(this.props.valorB)
            })
            break
        case "C":
            this.setState({
                valorAInput: "",
                valorBInput: "",
                resultado: ""
            })
        break
    }
  }

    handleValorAFieldChange = (arg) => {
        this.setState({
            valorAInput: arg.target.value
        })
    }

    handleValorBFieldChange = (arg) => {
        this.setState({
            valorBInput: arg.target.value
        })
    }
}

export default NewCalculator;
